 READ TABLE lt_kna1 ASSIGNING &lt;kna1&gt; WITH KEY kunnr =
&lt;vbak&gt;-kunnr.  
 IF sy-subrc EQ 0.  
 ls_order-cust-name1 = &lt;kna1&gt;-name1.  
 ls_order-cust-stras = &lt;kna1&gt;-stras.  
 ls_order-cust-ort01 = &lt;kna1&gt;-ort01.  
 ls_order-cust-regio = &lt;kna1&gt;-regio.  
 ls_order-cust-land1 = &lt;kna1&gt;-land1.  
 ENDIF.  
 CONCATENATE 'Sales Order' &lt;vbak&gt;-vbeln INTO ls_order-title SEPARATED
BY space.  
 APPEND ls_order TO &lt;orders&gt;.  
 ENDLOOP.  
ENDMETHOD.





